import React, { useState } from 'react';
import TranslatorBox from '../components/TranslatorBox';
import VoiceTranslator from '../components/VoiceTranslator';
import ClipboardWatcher from '../components/ClipboardWatcher';
import TranslationHistory from '../components/TranslationHistory';
import ThemeSwitcher from '../components/ThemeSwitcher';

const Home = ({ theme, toggleTheme }) => {
  const [activeTab, setActiveTab] = useState('text');
  const [targetLang, setTargetLang] = useState('es'); // idioma al que traducir

  return (
    <div className="min-h-screen p-6 bg-white dark:bg-gray-900 text-black dark:text-white">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">üåç TranslateX</h1>
        <ThemeSwitcher theme={theme} toggleTheme={toggleTheme} />
      </div>

      <div className="flex gap-4 mb-6 border-b dark:border-gray-700 pb-2">
        <button onClick={() => setActiveTab('text')} className={`px-4 py-2 rounded ${activeTab === 'text' ? 'bg-blue-500 text-white' : 'bg-gray-200 dark:bg-gray-700'}`}>Texto</button>
        <button onClick={() => setActiveTab('voice')} className={`px-4 py-2 rounded ${activeTab === 'voice' ? 'bg-blue-500 text-white' : 'bg-gray-200 dark:bg-gray-700'}`}>Voz</button>
        <button onClick={() => setActiveTab('clipboard')} className={`px-4 py-2 rounded ${activeTab === 'clipboard' ? 'bg-blue-500 text-white' : 'bg-gray-200 dark:bg-gray-700'}`}>Portapapeles</button>
        <button onClick={() => setActiveTab('history')} className={`px-4 py-2 rounded ${activeTab === 'history' ? 'bg-blue-500 text-white' : 'bg-gray-200 dark:bg-gray-700'}`}>Historial</button>
      </div>

      {activeTab === 'text' && <TranslatorBox targetLang={targetLang} setTargetLang={setTargetLang} />}
      {activeTab === 'voice' && <VoiceTranslator targetLang={targetLang} />}
      {activeTab === 'clipboard' && <ClipboardWatcher targetLang={targetLang} />}
      {activeTab === 'history' && <TranslationHistory />}
    </div>
  );
};

export default Home;
