import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';

const VoiceTranslator = () => {
  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [speechText, setSpeechText] = useState('');
  const [translatedText, setTranslatedText] = useState('');
  const [targetLang, setTargetLang] = useState('es');

  useEffect(() => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert('Tu navegador no soporta reconocimiento de voz.');
      return;
    }

    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.lang = 'en-US';
    recognitionRef.current.continuous = false;
    recognitionRef.current.interimResults = false;

    recognitionRef.current.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setSpeechText(transcript);
      translateText(transcript);
    };

    recognitionRef.current.onend = () => {
      setIsListening(false);
    };
  }, []);

  const handleStartListening = () => {
    try {
      if (recognitionRef.current && !isListening) {
        setIsListening(true);
        recognitionRef.current.start();
      }
    } catch (err) {
      console.error('Error al iniciar reconocimiento:', err.message);
    }
  };

  const handleStopListening = () => {
    if (recognitionRef.current && isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
    }
  };

  const translateText = async (text) => {
    try {
      const res = await axios.post('https://translate.argosopentech.com/translate', {
        q: text,
        source: 'auto',
        target: targetLang,
        format: 'text',
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      setTranslatedText(res.data.translatedText);
    } catch (error) {
      console.error('Error al traducir:', error);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      <h1 className="text-2xl font-bold mb-4 text-center">Traductor por Voz</h1>

      <div className="flex gap-4 mb-4">
        <button
          onClick={handleStartListening}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          üéôÔ∏è Empezar a Hablar
        </button>
        <button
          onClick={handleStopListening}
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          ‚õî Detener
        </button>
      </div>

      <select
        value={targetLang}
        onChange={(e) => setTargetLang(e.target.value)}
        className="mb-4 p-2 border rounded"
      >
        <option value="es">Espa√±ol</option>
        <option value="en">Ingl√©s</option>
        <option value="fr">Franc√©s</option>
        <option value="de">Alem√°n</option>
        <option value="it">Italiano</option>
        <option value="pt">Portugu√©s</option>
      </select>

      <div className="w-full max-w-md">
        <p className="mb-2 font-semibold">Texto reconocido:</p>
        <div className="bg-white p-3 rounded shadow mb-4">{speechText || '...'}</div>

        <p className="mb-2 font-semibold">Traducci√≥n:</p>
        <div className="bg-white p-3 rounded shadow">{translatedText || '...'}</div>
      </div>
    </div>
  );
};

export default VoiceTranslator;
