import React, { useRef, useState, useEffect } from 'react';

const idiomas = [
  { code: 'es', label: 'Espa√±ol' },
  { code: 'en', label: 'Ingl√©s' },
  { code: 'fr', label: 'Franc√©s' },
  { code: 'de', label: 'Alem√°n' },
  // Agrega m√°s idiomas si quieres
];

const VoiceTranslator = () => {
  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [translatedText, setTranslatedText] = useState('');
  const [inputLang, setInputLang] = useState('es'); // idioma de entrada
  const [outputLang, setOutputLang] = useState('en'); // idioma de salida

  // Iniciar reconocimiento de voz
  const startListening = () => {
    if (!('webkitSpeechRecognition' in window)) {
      alert('Tu navegador no soporta reconocimiento de voz.');
      return;
    }

    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = inputLang === 'es' ? 'es-ES' : inputLang === 'en' ? 'en-US' : inputLang;
    recognition.interimResults = false;
    recognition.continuous = false;

    recognition.onresult = (event) => {
      const result = event.results[0][0].transcript;
      setTranscript(result);
    };

    recognition.onerror = (event) => {
      console.error('Error de reconocimiento de voz:', event.error);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognitionRef.current = recognition;
    recognition.start();
    setIsListening(true);
  };

  // Parar reconocimiento
  const stopListening = () => {
    recognitionRef.current?.stop();
    setIsListening(false);
  };

  // Traducci√≥n autom√°tica al detectar texto usando MyMemory
  useEffect(() => {
    const translateText = async () => {
      if (transcript) {
        try {
          const response = await fetch(
            `https://api.mymemory.translated.net/get?q=${encodeURIComponent(transcript)}&langpair=${inputLang}|${outputLang}`
          );
          const data = await response.json();
          setTranslatedText(data.responseData.translatedText);
        } catch (error) {
          console.error('Error al traducir:', error);
          setTranslatedText('‚ùå Error al traducir');
        }
      }
    };

    translateText();
  }, [transcript, inputLang, outputLang]);

  return (
    <div className="p-4 bg-white dark:bg-gray-800 rounded shadow">
      <div className="flex items-center gap-4 mb-4">
        <button
          onClick={isListening ? stopListening : startListening}
          className={`px-4 py-2 rounded font-bold transition ${
            isListening ? 'bg-red-500 text-white' : 'bg-green-500 text-white'
          }`}
        >
          {isListening ? 'üõë Detener' : 'üé§ Hablar'}
        </button>
        <span className="text-gray-600 dark:text-gray-300">
          {isListening ? 'Escuchando...' : 'Presiona para comenzar'}
        </span>
        <select
          value={inputLang}
          onChange={e => setInputLang(e.target.value)}
          className="ml-4 px-2 py-1 rounded"
        >
          {idiomas.map(lang => (
            <option key={lang.code} value={lang.code}>{lang.label}</option>
          ))}
        </select>
        <span className="text-xs text-gray-500">Idioma de entrada</span>
        <select
          value={outputLang}
          onChange={e => setOutputLang(e.target.value)}
          className="ml-4 px-2 py-1 rounded"
        >
          {idiomas.map(lang => (
            <option key={lang.code} value={lang.code}>{lang.label}</option>
          ))}
        </select>
        <span className="text-xs text-gray-500">Idioma de salida</span>
      </div>

      <div className="mb-4">
        <h2 className="font-semibold mb-1">üìù Texto detectado:</h2>
        <div className="p-3 bg-gray-100 dark:bg-gray-700 rounded text-gray-900 dark:text-white">
          {transcript || '...'}
        </div>
      </div>

      <div>
        <h2 className="font-semibold mb-1">üåê Traducci√≥n:</h2>
        <div className="p-3 bg-gray-100 dark:bg-gray-700 rounded text-gray-900 dark:text-white">
          {translatedText || '...'}
        </div>
      </div>
    </div>
  );
};

export default VoiceTranslator;
