// src/components/VoiceTranslator.jsx
import { useRef, useState } from 'react';
import axios from 'axios';

const VoiceTranslator = () => {
  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [translated, setTranslated] = useState('');
  const [lang, setLang] = useState('es');

  const startListening = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert('Tu navegador no soporta reconocimiento de voz.');
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.interimResults = false;

    recognition.onresult = async (event) => {
      const voiceText = event.results[0][0].transcript;
      setTranscript(voiceText);

      try {
        const res = await axios.post('https://libretranslate.de/translate', {
          q: voiceText,
          source: 'auto',
          target: lang,
          format: 'text'
        });

        setTranslated(res.data.translatedText);
      } catch (err) {
        console.error(err);
      }
    };

    recognitionRef.current = recognition;
    recognition.start();
    setIsListening(true);

    recognition.onend = () => setIsListening(false);
  };

  return (
    <div className="p-4 bg-white rounded-xl shadow-xl max-w-xl mx-auto mt-6">
      <h2 className="text-xl font-bold mb-4">Traductor por Voz</h2>
      <select
        value={lang}
        onChange={(e) => setLang(e.target.value)}
        className="p-2 border rounded mb-2 w-full"
      >
        <option value="es">Español</option>
        <option value="en">Inglés</option>
        <option value="fr">Francés</option>
        <option value="de">Alemán</option>
      </select>
      <button
        onClick={startListening}
        disabled={isListening}
        className="bg-green-600 text-white px-4 py-2 rounded w-full hover:bg-green-700"
      >
        {isListening ? 'Escuchando...' : 'Hablar'}
      </button>
      {transcript && (
        <div className="mt-4">
          <strong>Dijiste:</strong> <p>{transcript}</p>
        </div>
      )}
      {translated && (
        <div className="mt-2 p-3 bg-gray-100 rounded">
          <strong>Traducción:</strong>
          <p>{translated}</p>
        </div>
      )}
    </div>
  );
};

export default VoiceTranslator;
