import React, { useRef, useState, useEffect } from 'react';
import axios from 'axios';

const VoiceTranslator = ({ targetLang = 'es' }) => {
  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [translatedText, setTranslatedText] = useState('');

  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert('Tu navegador no soporta reconocimiento de voz.');
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.interimResults = false;
    recognition.continuous = false;

    recognition.onresult = async (event) => {
      const text = event.results[0][0].transcript;
      setTranscript(text);
      await translateText(text);
    };

    recognition.onerror = (event) => {
      console.error('Error de reconocimiento:', event.error);
      setIsListening(false);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognitionRef.current = recognition;
  }, [targetLang]);

  const startListening = () => {
    if (recognitionRef.current) {
      setTranscript('');
      setTranslatedText('');
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  const translateText = async (text) => {
    try {
      const response = await axios.post('https://libretranslate.de/translate', {
        q: text,
        source: 'en',
        target: targetLang,
        format: 'text',
      }, {
        headers: { 'Content-Type': 'application/json' }
      });
      setTranslatedText(response.data.translatedText);
    } catch (error) {
      console.error('Error al traducir:', error);
      setTranslatedText('‚ùå Error al traducir');
    }
  };

  return (
    <div className="p-6 bg-white dark:bg-gray-800 rounded-2xl shadow-md space-y-4">
      <h2 className="text-xl font-bold">üé§ Traductor por Voz</h2>
      <button
        onClick={startListening}
        disabled={isListening}
        className="bg-indigo-600 hover:bg-indigo-700 text-white py-2 px-4 rounded-xl shadow"
      >
        {isListening ? 'üéôÔ∏è Escuchando...' : 'üéôÔ∏è Hablar en Ingl√©s'}
      </button>

      <div>
        <h4 className="font-semibold text-gray-700 dark:text-gray-300">üìù Detectado:</h4>
        <p className="bg-gray-100 dark:bg-gray-700 p-3 rounded">{transcript}</p>
      </div>

      <div>
        <h4 className="font-semibold text-gray-700 dark:text-gray-300">üåê Traducci√≥n:</h4>
        <p className="bg-green-100 dark:bg-green-700 p-3 rounded">{translatedText}</p>
      </div>
    </div>
  );
};

export default VoiceTranslator;
