// src/components/TextTranslator.jsx
import { useState } from 'react';
import axios from 'axios';

const TextTranslator = () => {
  const [input, setInput] = useState('');
  const [translated, setTranslated] = useState('');
  const [lang, setLang] = useState('en');

  const handleTranslate = async () => {
    try {
      const res = await axios.post('https://libretranslate.de/translate', {
        q: input,
        source: 'auto',
        target: lang,
        format: 'text'
      }, {
        headers: { 'accept': 'application/json' }
      });

      setTranslated(res.data.translatedText);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="p-4 bg-white rounded-xl shadow-xl max-w-xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Traductor de Texto</h2>
      <textarea
        value={input}
        onChange={(e) => setInput(e.target.value)}
        className="w-full p-2 border rounded mb-2"
        placeholder="Escribí el texto a traducir..."
        rows={4}
      />
      <select
        value={lang}
        onChange={(e) => setLang(e.target.value)}
        className="p-2 border rounded mb-2 w-full"
      >
        <option value="en">Inglés</option>
        <option value="es">Español</option>
        <option value="fr">Francés</option>
        <option value="de">Alemán</option>
        <option value="it">Italiano</option>
        <option value="pt">Portugués</option>
      </select>
      <button
        onClick={handleTranslate}
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 w-full"
      >
        Traducir
      </button>
      {translated && (
        <div className="mt-4 p-3 bg-gray-100 rounded">
          <strong>Traducción:</strong>
          <p>{translated}</p>
        </div>
      )}
    </div>
  );
};

export default TextTranslator;
