// VoiceTranslator.jsx
import React, { useState, useRef, useEffect, useCallback } from 'react';
import axios from 'axios';

const VoiceTranslator = () => {
  const [speechText, setSpeechText] = useState('');
  const [translatedText, setTranslatedText] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [targetLang, setTargetLang] = useState('es');
  const recognitionRef = useRef(null);

  const translateText = useCallback(async (text) => {
    try {
      const res = await axios.post(
        'https://translate.argosopentech.com/translate',
        {
          q: text,
          source: 'auto',
          target: targetLang,
          format: 'text',
        },
        {
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );
      setTranslatedText(res.data.translatedText);
    } catch (error) {
      console.error('Error al traducir:', error);
    }
  }, [targetLang]);

  useEffect(() => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert('Tu navegador no soporta reconocimiento de voz.');
      return;
    }

    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.lang = 'en-US';
    recognitionRef.current.continuous = false;
    recognitionRef.current.interimResults = false;

    recognitionRef.current.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setSpeechText(transcript);
      translateText(transcript);
    };

    recognitionRef.current.onend = () => {
      setIsListening(false);
    };
  }, [translateText]);

  const startListening = () => {
    if (recognitionRef.current && !isListening) {
      setIsListening(true);
      recognitionRef.current.start();
    }
  };

  return (
    <div className="p-6 max-w-lg mx-auto bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Traductor por voz</h2>

      <div className="mb-4">
        <label htmlFor="language" className="block mb-2 font-medium">
          Seleccionar idioma de destino:
        </label>
        <select
          id="language"
          value={targetLang}
          onChange={(e) => setTargetLang(e.target.value)}
          className="border p-2 rounded w-full"
        >
          <option value="es">Español</option>
          <option value="en">Inglés</option>
          <option value="fr">Francés</option>
          <option value="de">Alemán</option>
        </select>
      </div>

      <button
        onClick={startListening}
        disabled={isListening}
        className={`w-full p-2 rounded text-white ${
          isListening ? 'bg-gray-400' : 'bg-blue-500 hover:bg-blue-600'
        }`}
      >
        {isListening ? 'Escuchando...' : 'Hablar'}
      </button>

      <div className="mt-4">
        <h3 className="font-semibold">Texto reconocido:</h3>
        <p className="border p-2 rounded bg-gray-100">{speechText}</p>
      </div>

      <div className="mt-4">
        <h3 className="font-semibold">Traducción:</h3>
        <p className="border p-2 rounded bg-green-100">{translatedText}</p>
      </div>
    </div>
  );
};

export default VoiceTranslator;
