import React, { useRef, useState, useEffect } from 'react';
import axios from 'axios';

const VoiceTranslator = ({ targetLang }) => {
  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [translatedText, setTranslatedText] = useState('');

  // Iniciar reconocimiento de voz
  const startListening = () => {
    if (!('webkitSpeechRecognition' in window)) {
      alert('Tu navegador no soporta reconocimiento de voz.');
      return;
    }

    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = 'es-ES';
    recognition.interimResults = false;
    recognition.continuous = false;

    recognition.onresult = (event) => {
      const result = event.results[0][0].transcript;
      setTranscript(result);
    };

    recognition.onerror = (event) => {
      console.error('Error de reconocimiento de voz:', event.error);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognitionRef.current = recognition;
    recognition.start();
    setIsListening(true);
  };

  // Parar reconocimiento
  const stopListening = () => {
    recognitionRef.current?.stop();
    setIsListening(false);
  };

  // Traducci√≥n autom√°tica al detectar texto
  useEffect(() => {
    const translateText = async () => {
      if (transcript) {
        try {
          const response = await axios.post(
            'https://libretranslate.de/translate',
            {
              q: transcript,
              source: 'auto',
              target: targetLang,
              format: 'text',
            },
            {
              headers: {
                'Content-Type': 'application/json',
              },
            }
          );
          console.log('Respuesta de traducci√≥n:', response.data);
          setTranslatedText(response.data.translatedText);
        } catch (error) {
          console.error('Error al traducir:', error);
          if (error.response) {
            console.error('Respuesta del servidor:', error.response.data);
            setTranslatedText(
              `‚ùå Error: ${error.response.data.error || 'al traducir'}`
            );
          } else {
            setTranslatedText('‚ùå Error al traducir');
          }
        }
      }
    };

    translateText();
  }, [transcript, targetLang]);

  return (
    <div className="p-4 bg-white dark:bg-gray-800 rounded shadow">
      <div className="flex items-center gap-4 mb-4">
        <button
          onClick={isListening ? stopListening : startListening}
          className={`px-4 py-2 rounded font-bold transition ${
            isListening ? 'bg-red-500 text-white' : 'bg-green-500 text-white'
          }`}
        >
          {isListening ? 'üõë Detener' : 'üé§ Hablar'}
        </button>
        <span className="text-gray-600 dark:text-gray-300">
          {isListening ? 'Escuchando...' : 'Presiona para comenzar'}
        </span>
      </div>

      <div className="mb-4">
        <h2 className="font-semibold mb-1">üìù Texto detectado:</h2>
        <div className="p-3 bg-gray-100 dark:bg-gray-700 rounded text-gray-900 dark:text-white">
          {transcript || '...'}
        </div>
      </div>

      <div>
        <h2 className="font-semibold mb-1">üåê Traducci√≥n:</h2>
        <div className="p-3 bg-gray-100 dark:bg-gray-700 rounded text-gray-900 dark:text-white">
          {translatedText || '...'}
        </div>
      </div>
    </div>
  );
};

export default VoiceTranslator;
