import React from 'react';

const ThemeSwitcher = ({ theme, toggleTheme }) => {
  return (
    <button
      onClick={toggleTheme}
      className="text-sm px-4 py-2 rounded-xl bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-white hover:opacity-80 shadow"
    >
      {theme === 'light' ? 'üåô Modo Oscuro' : '‚òÄÔ∏è Modo Claro'}
    </button>
  );
};

export default ThemeSwitcher;
