import React, { useRef, useState, useEffect } from 'react';
import axios from 'axios';

const VoiceTranslator = () => {
  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [translatedText, setTranslatedText] = useState('');

  useEffect(() => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert('Tu navegador no soporta reconocimiento de voz.');
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US'; // idioma de entrada
    recognition.interimResults = false;
    recognition.continuous = false;

    recognition.onresult = async (event) => {
      const text = event.results[0][0].transcript;
      setTranscript(text);
      await translateText(text);
    };

    recognition.onerror = (event) => {
      console.error('Error de reconocimiento:', event.error);
      setIsListening(false);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognitionRef.current = recognition;
  }, []);

  const startListening = () => {
    if (!isListening && recognitionRef.current) {
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  const translateText = async (text) => {
    try {
      const response = await axios.post(
        'https://cors-anywhere.herokuapp.com/https://translate.argosopentech.com/translate',
        {
          q: text,
          source: 'en',
          target: 'es',
          format: 'text',
        },
        {
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );

      setTranslatedText(response.data.translatedText);
    } catch (error) {
      console.error('Error al traducir:', error);
      setTranslatedText('Error al traducir');
    }
  };

  return (
    <div className="p-4 bg-white rounded shadow max-w-xl mx-auto mt-10">
      <h1 className="text-2xl font-bold mb-4">Traductor de Voz</h1>
      <button
        onClick={startListening}
        className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        disabled={isListening}
      >
        {isListening ? 'Escuchando...' : 'Hablar en inglés'}
      </button>

      <div className="mt-4">
        <p className="text-gray-600 font-semibold">Texto detectado:</p>
        <p className="bg-gray-100 p-2 rounded">{transcript}</p>
      </div>

      <div className="mt-4">
        <p className="text-gray-600 font-semibold">Traducción:</p>
        <p className="bg-green-100 p-2 rounded">{translatedText}</p>
      </div>
    </div>
  );
};

export default VoiceTranslator;
