import React, { useState, useEffect } from 'react';
import TranslatorBox from '../components/TranslatorBox';
import VoiceTranslator from '../components/VoiceTranslator';
import ClipboardWatcher from '../components/ClipboardWatcher';
import TranslationHistory from '../components/TranslationHistory';
import ThemeSwitcher from '../components/ThemeSwitcher';

const Home = ({ theme, toggleTheme }) => {
  const [activeTab, setActiveTab] = useState('text');
  const [targetLang, setTargetLang] = useState('en');

  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
  }, [theme]);

  return (
    <div className="min-h-screen p-6 bg-white dark:bg-gray-900 text-black dark:text-white">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">üåç TranslateX</h1>
        <ThemeSwitcher theme={theme} toggleTheme={toggleTheme} />
      </div>

      <div className="flex gap-4 mb-6 border-b pb-4">
        {['text', 'voice', 'clipboard', 'history'].map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`px-5 py-2 rounded-full font-semibold transition
              ${activeTab === tab
                ? 'bg-indigo-600 text-white shadow'
                : 'bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-white hover:opacity-80'}`}
          >
            {tab === 'text' && 'üìù Texto'}
            {tab === 'voice' && 'üé§ Voz'}
            {tab === 'clipboard' && 'üìã Portapapeles'}
            {tab === 'history' && 'üìö Historial'}
          </button>
        ))}
      </div>

      {activeTab === 'text' && <TranslatorBox targetLang={targetLang} setTargetLang={setTargetLang} />}
      {activeTab === 'voice' && <VoiceTranslator targetLang={targetLang} />}
      {activeTab === 'clipboard' && <ClipboardWatcher targetLang={targetLang} />}
      {activeTab === 'history' && <TranslationHistory />}
    </div>
  );
};

export default Home;
