import React, { useState } from 'react';

const TranslatorBox = ({ targetLang, setTargetLang }) => {
  const [text, setText] = useState('');
  const [translatedText, setTranslatedText] = useState('');

  const translateText = async () => {
    if (!text.trim()) return;
    try {
      const response = await fetch(`https://api.mymemory.translated.net/get?q=${encodeURIComponent(text)}&langpair=es|${targetLang}`);
      const data = await response.json();
      const result = data.responseData.translatedText;
      setTranslatedText(result);
      saveToHistory(text, result);
    } catch (error) {
      console.error('Error al traducir:', error);
    }
  };

  const saveToHistory = (original, translated) => {
    const current = JSON.parse(localStorage.getItem('translate_history')) || [];
    const timestamp = new Date().toLocaleString();
    const updated = [...current, { original, translated, timestamp }];
    localStorage.setItem('translate_history', JSON.stringify(updated));
  };

  return (
    <div className="p-6 bg-white dark:bg-gray-800 rounded-2xl shadow-md space-y-4">
      <select
        value={targetLang}
        onChange={(e) => setTargetLang(e.target.value)}
        className="w-full p-2 rounded border dark:bg-gray-700 dark:text-white"
      >
        <option value="es">Español</option>
        <option value="en">Inglés</option>
        <option value="fr">Francés</option>
        <option value="pt">Portugués</option>
        <option value="de">Alemán</option>
        <option value="it">Italiano</option>
        <option value="ja">Japonés</option>
      </select>

      <textarea
        className="w-full p-3 border rounded dark:bg-gray-700 dark:text-white"
        rows="4"
        placeholder="Texto en español..."
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <button
        onClick={translateText}
        className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-xl shadow transition"
      >
        Traducir
      </button>
      {translatedText && (
        <div className="p-3 border rounded bg-green-100 dark:bg-green-800 dark:text-white">
          <p>{translatedText}</p>
        </div>
      )}
    </div>
  );
};

export default TranslatorBox;
