import React, {  useRef, useState } from 'react';
import axios from 'axios';

const Translator = () => {
  const recognitionRef = useRef(null);
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [translatedText, setTranslatedText] = useState('');

  const startListening = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert('Tu navegador no soporta reconocimiento de voz');
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.continuous = false;

    recognition.onresult = async (event) => {
      const text = event.results[0][0].transcript;
      setTranscript(text);
      translateText(text);
    };

    recognition.onerror = (event) => console.error('Error:', event.error);
    recognition.onend = () => setIsListening(false);

    recognition.start();
    setIsListening(true);
    recognitionRef.current = recognition;
  };

  const stopListening = () => {
    recognitionRef.current?.stop();
    setIsListening(false);
  };

  const translateText = async (text) => {
    try {
      const res = await axios.post('https://libretranslate.de/translate', {
        q: text,
        source: 'en',
        target: 'es',
        format: 'text',
      }, {
        headers: { accept: 'application/json' }
      });
      setTranslatedText(res.data.translatedText);
    } catch (err) {
      console.error('Error al traducir:', err);
    }
  };

  return (
    <div className="p-8 bg-gray-100 min-h-screen font-sans">
      <h1 className="text-2xl font-bold mb-4">TranslateX</h1>

      <textarea
        placeholder="EscribÃ­ texto en inglÃ©s..."
        value={transcript}
        onChange={(e) => {
          setTranscript(e.target.value);
          translateText(e.target.value);
        }}
        className="w-full p-4 border rounded mb-4"
        rows="4"
      />

      <button
        onClick={isListening ? stopListening : startListening}
        className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        ðŸŽ¤ {isListening ? 'Detener' : 'Hablar'}
      </button>

      <div className="mt-6">
        <h2 className="font-semibold text-lg">TraducciÃ³n:</h2>
        <p className="bg-white border p-4 rounded mt-2">{translatedText}</p>
      </div>
    </div>
  );
};

export default Translator;
