import React, { useEffect, useState } from 'react';

const TranslationHistory = () => {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem('translate_history')) || [];
    setHistory(stored.reverse());
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Historial de Traducciones</h2>
      {history.length === 0 ? (
        <p className="text-gray-500">Aún no hay traducciones guardadas.</p>
      ) : (
        <ul className="space-y-2 max-h-[400px] overflow-y-auto">
          {history.map((item, index) => (
            <li key={index} className="p-3 bg-gray-100 dark:bg-gray-700 rounded shadow text-sm">
              <p><strong>Texto:</strong> {item.original}</p>
              <p><strong>Traducción:</strong> {item.translated}</p>
              <p className="text-xs text-gray-400">{item.timestamp}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TranslationHistory;
